mass_values = aa_masses.values()

masses = aa_masses
masses_integers = {}

for e in masses:
    masses_integers[e] = int(masses[e])

# create a dictionary with the masses as keys and AAs as values
reversed_masses = {}
for amino_acid in masses_integers: 
    mass = masses_integers[amino_acid]
    if masses_integers[amino_acid] in reversed_masses:
        reversed_masses[mass].append(amino_acid)
    else: 
        reversed_masses[mass] = [amino_acid]

