"""
Solution to the Converting a Peptide Vector into a Peptide Problem.
Molecular Evolution (Bioinformatics IV) on Coursera.org
Week 4, code challenge #4
"""

import constants as co

def vector_to_peptide(input_vector):
    '''
    Converting a Peptide Vector into a Peptide Problem.
    
    Parameters
    --------
    input_vector: A space-delimited binary vector P. (string)
    
    Return
    --------
    An amino acid string whose binary peptide vector matches P. (string)
    '''
    def convert_input(input_data):
        '''Converts input for vector_to_peptide.'''
        split_data = input_data.split()
        vector = []
        for e in split_data:
            vector.append(int(e))
        return vector

    def convert_to_spectrum(vector):
        '''
        Used with vector_to_peptide. Converts a vector showing the distribution of masses in a spectrum
        to the corresponding spectrum.
        '''
        spectrum = []
        for i in range(0,len(vector)):
            if vector[i] == 1:
                spectrum.append(i+1)
        return spectrum
        
    vector = convert_input(input_vector)
    spectrum = [0] + convert_to_spectrum(vector)
    peptide = ''
    
    # find the mass difference between every set of values in the spectrum and add the amino acid corresponding to that mass to the peptide
    for i in range(1,len(spectrum)):
        amino_acid_mass = spectrum[i] - spectrum[i-1]
        amino_acid = co.reversed_masses[amino_acid_mass][0]
        peptide += amino_acid
        
    return peptide

